INSERT INTO `irmr_posts` VALUES('5', '1', '2015-03-20 18:44:26', '2015-03-20 18:44:26', '<h4><span class=\"span\">Welcome to</span>Idaho Rocky Mountain Ranch</h4>\r\n\r\nThe living history of the Old West mingles with the solitude and peace of this 900-acre ranch, offering <strong>freedom</strong>, breathtaking <strong>natural beauty</strong>, and a <strong>relaxed atmosphere</strong>.', 'Home', '', 'publish', 'open', 'open', '', 'home', '', '', '2015-04-01 17:07:23', '2015-04-01 17:07:23', '', '0', 'http://localhost:8888/?page_id=5', '0', 'page', '', '0');
