INSERT INTO `irmr_posts` VALUES('7', '1', '2015-03-20 18:46:10', '2015-03-20 18:46:10', '<strong>Surrounded by the largest protected wilderness area in the lower 48, in the spectacular Sawtooth National Recreation Area, you will be enticed to some of the best outdoor recreation in the world, without the crowds.</strong>\r\n\r\nExplore unlimited opportunities for western adventure, with choices from extreme physical challenges to soothing relaxation.\r\n\r\n[overview]\r\n[overview_page img=\"/img/overview-theranchexperience.jpg\" title=\"The Ranch Experience\" link=\"/the-experience/the-ranch-experience/\"]Nestled amidst Idaho’s snowcapped Rocky Mountain peaks and the meandering Salmon River, the Idaho Rocky Mountain Ranch has flourished as a western ranch vacation retreat for eighty years.[/overview_page]\r\n\r\n[overview_page img=\"/img/overview-adayinthelife.jpg\" title=\"A Day in <span>the Life</span>\" link=\"/the-experience/a-day-in-the-life/\"]Explore a day in the life at Idaho Rocky Mountain Ranch.[/overview_page]\r\n\r\n[overview_page img=\"/img/overview-ourflexibleapproach.jpg\" title=\"Our Flexible <span>Approach</span>\" link=\"/the-experience/our-flexible-approach/\"]Here at the Idaho Rocky Mountain Ranch, we make sure that freedom guides your adventures, enjoyed at your own pace and time.[/overview_page]\r\n\r\n[overview_page img=\"/img/overview-theidahorockies.jpg\" title=\"The Idaho <span>Rockies</span>\" link=\"/the-experience/the-idaho-rockies/\"]Surrounded by more wilderness than anywhere else in the continental United States, the Idaho Rocky Mountain Ranch is the ideal home base to explore the adventure-rich Rocky Mountains of Idaho.[/overview_page]\r\n\r\n[overview_page img=\"/img/overview-ourrichhistory.jpg\" title=\"Our <span>Rich History</span>\" link=\"/the-experience/our-rich-history/\"]The Idaho Rocky Mountain Club opened to guests June 25, 1930 with the completion of the main lodge, nine guest cabins, a hydroelectric plant, fishpond, and hot springs pool.[/overview_page]\r\n[/overview]', 'The Experience', '', 'publish', 'open', 'open', '', 'the-experience', '', '', '2015-04-01 17:32:19', '2015-04-01 17:32:19', '', '0', 'http://localhost:8888/?page_id=7', '0', 'page', '', '0');
