INSERT INTO `irmr_posts` VALUES('85', '1', '2015-03-20 21:43:01', '2015-03-20 21:43:01', '<a href=\"http://irmr.zendentest.com/dining/sample-menus/lodge-dining-menu-sampler/\" title=\"Lodge Dining Menu Sampler\">Lodge Dining Menu Sampler</a>\r\n<a href=\"http://irmr.zendentest.com/dining/sample-menus/lunch-menu/\" title=\"Lunch Menu\">Lunch Menu</a>\r\n<a href=\"http://irmr.zendentest.com/dining/sample-menus/breakfast-menu/\" title=\"Breakfast Menu\">Breakfast Menu</a>\r\n<a href=\"http://irmr.zendentest.com/dining/sample-menus/southwest-fajita-grill/\" title=\"Southwest Fajita Grill\">Southwest Fajita Grill</a>\r\n<a href=\"http://irmr.zendentest.com/dining/sample-menus/western-bbq/\" title=\"Western BBQ\">Western BBQ</a>\r\n<a href=\"http://irmr.zendentest.com/dining/sample-menus/wood-fire-brick-oven-delicacies/\" title=\"Wood Fire Brick Oven Delicacies\">Wood Fired Brick Oven Delicacies</a>\r\n<a href=\"http://irmr.zendentest.com/dining/sample-menus/dutch-oven/\" title=\"Dutch Oven\">Dutch Oven</a>\r\n<a href=\"http://irmr.zendentest.com/dining/sample-menus/childrens-menu/\" title=\"Children’s Menu\">Children’s Menu</a>', 'Sample Menus', '', 'publish', 'open', 'open', '', 'sample-menus', '', '', '2015-04-01 17:47:12', '2015-04-01 17:47:12', '', '81', 'http://irmr.zendentest.com/?page_id=85', '0', 'page', '', '0');
