INSERT INTO `irmr_posts` VALUES('92', '1', '2015-03-20 21:47:08', '2015-03-20 21:47:08', '[posts]\r\n[list_press publication=\"New York Times\" logo=\"/img/press/newyorktimes.jpg\"]\r\n[list_press publication=\"Sunset Magazine\" logo=\"/img/press/sunset.jpg\"]\r\n[list_press publication=\"Los Angeles Times\" logo=\"/img/press/latimes.jpg\"]\r\n[list_press publication=\"Local\" logo=\"/img/press/local.jpg\"]\r\n[list_press publication=\"Idaho Statesmen\" logo=\"/img/press/idahostatesmen.jpg\"]\r\n[list_press publication=\"Seattle Times\" logo=\"/img/press/seattletimes.jpg\"]\r\n[list_press publication=\"Washington Post\" logo=\"/img/press/washingtonpost.jpg\"]\r\n[list_press publication=\"Travel and Leisure\" logo=\"/img/press/travel.jpg\"]\r\n[list_press publication=\"Sun Valley Property News\" logo=\"/img/press/sunvalleypropertynews.jpg\"]\r\n[list_press publication=\"Sun Valley Magazine\" logo=\"/img/press/sunvalleymagazine.jpg\"]\r\n[/posts]', 'Press', '', 'publish', 'open', 'open', '', 'press-articles', '', '', '2015-04-01 17:36:34', '2015-04-01 17:36:34', '', '0', 'http://irmr.zendentest.com/?page_id=92', '0', 'page', '', '0');
