INSERT INTO `irmr_posts` VALUES('410', '1', '2015-03-27 16:50:29', '2015-03-27 16:50:29', '<div class=\"menulist\">\r\n<h4>First Course</h4>\r\n<ul>\r\n<li><strong>Local White Corn Bisque</strong> with Grilled Lobster, Corn and Chive Salsa</li>\r\n<li><strong>IRMR Grilled Caesar</strong> with Grilled Crostini, Shaved Parmesan Reggiano, Organic Grape Tomatoes and Anchovies</li>\r\n</ul>\r\n</div>\r\n\r\n<div class=\"menulist\">\r\n<h4>Second Course</h4>\r\n<ul>\r\n<li><strong>Oregon Duck Two Ways</strong></li>\r\n<li><strong>Candied Breast & Crispy Confit Leg</strong></li>\r\n<li><strong>Grilled Fuji Apple & Garlic Chive Jus</strong></li>\r\n<li><strong>Acorn Squash Farrotto</strong></li>\r\n<li><strong>Local Haricot Verts</strong></li>\r\n<li><strong>Cast Iron Seared Pacific Sea Scallops</strong> with Huckleberry-Port Reduction, Sunchoke Mashers and Warm Spinach, Pancetta & Porcini Mushroom Salad</li>\r\n<li>Baked Lemon-Ricotta and Chive Free Form Ravioli</strong> with Idaho Morel Mushrooms, Leeks, Asparagus and Parmesan-Reggiano</li>\r\n</ul>\r\n</div>\r\n\r\n<div class=\"menulist\">\r\n<h4>Dessert</h4>\r\n<ul>\r\n<li><strong>Strawberry-Blueberry ShortCake</strong></li>\r\n<li><strong>Sweet Cream Biscuits</strong></li>\r\n<li><strong>Raspberry Sorbet and Chantilly Cream</strong></li>\r\n<li><strong>Molten Chocolate Cake</strong> w/ Toblerone Center, House Vanilla Ice Cream & Huckleberry Coulis</li>\r\n</ul>\r\n</div>', 'Lodge Dining Menu Sampler', '', 'publish', 'open', 'closed', '', 'lodge-dining-menu-sampler', '', '', '2015-04-01 17:51:10', '2015-04-01 17:51:10', '', '85', 'http://irmr.zendentest.com/?page_id=410', '0', 'page', '', '0');
