INSERT INTO `irmr_posts` VALUES('416', '1', '2015-03-27 17:06:11', '2015-03-27 17:06:11', '<div class=\"menulist\">\r\n<ul>\r\n<li><strong>IRMR Standard</strong>\r\nTwo Farm Fresh Eggs, Smoky Bacon or Montana Elk-Apple Sausage\r\nIdaho Baby Red Potatoes</li>\r\n<li><strong>Sawtooth Veggie Hash</strong>\r\nTwo Farm Fresh Eggs\r\nGrilled Broccolini, Leek, Bell Pepper, Marinated Tomato, Quinoa and Local Goat Cheese\r\n<em>Add Kurobuta Ham or House Smoked Trout</em></li>\r\n<li><strong>Cast-Iron Toasted Steel Cut Oatmeal</strong>\r\nToasted Hemp Seeds, Walnuts & Dried Fruit</li>\r\n<li><strong>Buttermilk Whole Wheat Pancakes or Waffles</strong>\r\nVermont Maple Syrup & Whipped Butter\r\nGet em’ with Blueberries, Chocolate Chips or IRMR Granola\r\n<em>Gluten Free Pancakes or Waffles Available upon Request</em></li>\r\n	<li><strong>Homemade Sourdough Grand Marnier French Toast</strong>\r\nFresh Strawberry & Honey Syrup</li>\r\n\r\n</ul>\r\n</div>\r\n\r\n<div class=\"menulist\">\r\n<h4>Hot Sides</h4>\r\n<ul>\r\n<li><strong>Idaho Baby Red Potatoes</strong></li>\r\n<li><strong>Smoky Bacon or Montana Elk-Apple Sausage</strong></li>\r\n<li><strong>Two Farm Fresh Eggs</strong></li>\r\n</ul>\r\n</div>\r\n', 'Breakfast Menu', '', 'publish', 'open', 'open', '', 'breakfast-menu', '', '', '2015-04-01 17:58:08', '2015-04-01 17:58:08', '', '85', 'http://irmr.zendentest.com/?page_id=416', '0', 'page', '', '0');
