INSERT INTO `irmr_posts` VALUES('437', '1', '2015-04-01 17:50:23', '2015-04-01 17:50:23', '<div class=\"menulist\">\n<h4>First Course</h4>\n<ul>\n<strong>Local White Corn Bisque</strong> with Grilled Lobster, Corn and Chive Salsa\n<strong>IRMR Grilled Caesar</strong> with Grilled Crostini, Shaved Parmesan Reggiano, Organic Grape Tomatoes and Anchovies\n</div>\n\n<div class=\"menulist\">\n<h4>Second Course</h4>\n<ul>\n<li><strong>Oregon Duck Two Ways</strong></li>\n<li><strong>Candied Breast & Crispy Confit Leg</strong></li>\n<li><strong>Grilled Fuji Apple & Garlic Chive Jus</strong></li>\n<li><strong>Acorn Squash Farrotto</strong></li>\n<li><strong>Local Haricot Verts</strong></li>\n<li><strong>Cast Iron Seared Pacific Sea Scallops</strong> with Huckleberry-Port Reduction, Sunchoke Mashers and Warm Spinach, Pancetta & Porcini Mushroom Salad</li>\n<li>Baked Lemon-Ricotta and Chive Free Form Ravioli</strong> with Idaho Morel Mushrooms, Leeks, Asparagus and Parmesan-Reggiano</li>\n</ul>\n</div>\n\n\n<h4>Dessert</h4>\n<strong>Strawberry-Blueberry ShortCake</strong>\n<strong>Sweet Cream Biscuits</strong>\n<strong>Raspberry Sorbet and Chantilly Cream</strong>\n<strong>Molten Chocolate Cake</strong> w/ Toblerone Center, House Vanilla Ice Cream & Huckleberry Coulis', 'Lodge Dining Menu Sampler', '', 'inherit', 'open', 'open', '', '410-autosave-v1', '', '', '2015-04-01 17:50:23', '2015-04-01 17:50:23', '', '410', 'http://irmr.zendentest.com/410-autosave-v1/', '0', 'revision', '', '0');
