INSERT INTO `irmr_posts` VALUES('439', '1', '2015-04-01 18:04:23', '2015-04-01 18:04:23', '<div class=\"menulist\">\n<ul>\n	<li><strong>Jordan’s Charcuterie Lunch</strong>\nChef Selection of Salami, Wedge of Cheese and Artisan Baguette\nPeach & Rosemary Jam, Cornichons and Whole Grain Mustard</li>\n<strong>Decker Peak Salad</strong>\n	<li>Organic Brown Rice, Apples, Local Kale, Avocado, Red Bell Peppers, White Cheddar, Toasted Walnuts,\nHemp Seeds tossed in an Apple-Sherry Vinaigrette\n<em>Add Roasted Turkey</em></li>\n        <li><strong>Williams Peak Salad</strong>\nOrganic Quinoa, Grapefruit, Sweet Peas, Cucumbers, Grape Tomatoes, Spinach, Sesame Seeds, Cashews tossed in a Yuzu Kosho-Grapefruit Vinaigrette\n<em>Add Roasted Turkey</em></li>\n</ul>\n</div>\n\n<div class=\"menulist\">\n<h4>Sandwiches</h4>\n<ul>\n	<li><em>All can be made with whole grain, gluten free bread.</em> </li>\n	<li><strong>Grand Mogul</strong>\nHouse Roasted Turkey, Smoked Provolone, Cranberry Marmalade, Tellicherry Pepper Aioli, Sprouts Marinated Tomatoes on Homemade Herb Focaccia</li>\n	<li><strong>Thompson Peak</strong>\nHouse Buffalo Pastrami, Grilled Red Onion & Fennel Relish, Daikon Radish Slaw, Roasted Garlic Mayo, Arugula, Oregon White Cheddar on Sourdough</li>\n	<li><strong>Mt Heyburn</strong>\nHouse Roast Turkey, Kurabuta Ham, Prosciutto, Peach & Rosemary Jam, Smoked Provolone, \nTellicherry Pepper Aioli, Avocado, Sprouts, Arugula on Multi-Grain</li>\n	<li><strong>Mt. Cramer</strong>\nRoasted Beets, Grilled Broccolini, Lemon Confit-Goat Cheese, Roasted Red Bell Pepper Relish, Marinated Tomatoes, Spinach, Sprouts, on Multi-Grain</li>\n</ul>\n</div>\n\n<div class=\"menulist\">\n<h4>Children’s Menu</h4>\n<em>Multi-Grain or Sourdough Bread</em>\n\n<strong>Homemade Peanut Butter</strong>\nWith Nutella, IRMR Strawberry or Huckleberry Jam\n\n<strong>Kurabuta Ham or House Roast Turkey</strong>\nWith Cheddar or Monterey Jack Cheese\n<em>Add Mayo, Mustard, Lettuce or Tomato</em>\n\n<strong>McDonald Peak Pasta Salad</strong>\nShell Pasta, Sweet Peas, Carrots, Grape Tomatoes, Apples & White Cheddar tossed in an Orange-Honey Vinaigrette\n<em>Add Roasted Turkey</em>', 'Lunch Menu', '', 'inherit', 'open', 'open', '', '413-autosave-v1', '', '', '2015-04-01 18:04:23', '2015-04-01 18:04:23', '', '413', 'http://irmr.zendentest.com/413-autosave-v1/', '0', 'revision', '', '0');
