INSERT INTO `irmr_posts` VALUES('611', '1', '2015-04-01 17:51:10', '2015-04-01 17:51:10', '<div class=\"menulist\">\r\n<h4>First Course</h4>\r\n<ul>\r\n<li><strong>Local White Corn Bisque</strong> with Grilled Lobster, Corn and Chive Salsa</li>\r\n<li><strong>IRMR Grilled Caesar</strong> with Grilled Crostini, Shaved Parmesan Reggiano, Organic Grape Tomatoes and Anchovies</li>\r\n</ul>\r\n</div>\r\n\r\n<div class=\"menulist\">\r\n<h4>Second Course</h4>\r\n<ul>\r\n<li><strong>Oregon Duck Two Ways</strong></li>\r\n<li><strong>Candied Breast & Crispy Confit Leg</strong></li>\r\n<li><strong>Grilled Fuji Apple & Garlic Chive Jus</strong></li>\r\n<li><strong>Acorn Squash Farrotto</strong></li>\r\n<li><strong>Local Haricot Verts</strong></li>\r\n<li><strong>Cast Iron Seared Pacific Sea Scallops</strong> with Huckleberry-Port Reduction, Sunchoke Mashers and Warm Spinach, Pancetta & Porcini Mushroom Salad</li>\r\n<li>Baked Lemon-Ricotta and Chive Free Form Ravioli</strong> with Idaho Morel Mushrooms, Leeks, Asparagus and Parmesan-Reggiano</li>\r\n</ul>\r\n</div>\r\n\r\n<div class=\"menulist\">\r\n<h4>Dessert</h4>\r\n<ul>\r\n<li><strong>Strawberry-Blueberry ShortCake</strong></li>\r\n<li><strong>Sweet Cream Biscuits</strong></li>\r\n<li><strong>Raspberry Sorbet and Chantilly Cream</strong></li>\r\n<li><strong>Molten Chocolate Cake</strong> w/ Toblerone Center, House Vanilla Ice Cream & Huckleberry Coulis</li>\r\n</ul>\r\n</div>', 'Lodge Dining Menu Sampler', '', 'inherit', 'open', 'open', '', '410-revision-v1', '', '', '2015-04-01 17:51:10', '2015-04-01 17:51:10', '', '410', 'http://irmr.zendentest.com/410-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `irmr_posts` VALUES('612', '1', '2015-04-01 17:57:30', '2015-04-01 17:57:30', '<div class=\"menulist\">\n<strong>IRMR Standard</strong>\nTwo Farm Fresh Eggs, Smoky Bacon or Montana Elk-Apple Sausage\nIdaho Baby Red Potatoes\n\n<strong>Sawtooth Veggie Hash</strong>\nTwo Farm Fresh Eggs\nGrilled Broccolini, Leek, Bell Pepper, Marinated Tomato, Quinoa and Local Goat Cheese\n<em>Add Kurobuta Ham or House Smoked Trout</em>\n\n<strong>Cast-Iron Toasted Steel Cut Oatmeal</strong>\nToasted Hemp Seeds, Walnuts & Dried Fruit\n\n<strong>Buttermilk Whole Wheat Pancakes or Waffles</strong>\nVermont Maple Syrup & Whipped Butter\nGet em’ with Blueberries, Chocolate Chips or IRMR Granola\n<em>Gluten Free Pancakes or Waffles Available upon Request</em>\n\n<strong>Homemade Sourdough Grand Marnier French Toast</strong>\nFresh Strawberry & Honey Syrup\n</div>\n\n<div class=\"menulist\">\n<h4>Hot Sides</h4>\n<ul>\n<li><strong>Idaho Baby Red Potatoes</strong></li>\n<li><strong>Smoky Bacon or Montana Elk-Apple Sausage</strong></li>\n<li><strong>Two Farm Fresh Eggs</strong></\n', 'Breakfast Menu', '', 'inherit', 'open', 'open', '', '416-autosave-v1', '', '', '2015-04-01 17:57:30', '2015-04-01 17:57:30', '', '416', 'http://irmr.zendentest.com/416-autosave-v1/', '0', 'revision', '', '0');
